class Solution:
    # @param A : tuple of integers
    # @return an integer
    def maxProfit(self, A):
        i = 0
        L = len(A)
        res = 0
        while i < L - 1:
            buyIndex = i
            while i < L -1 and A[i+1] <= A[i]:
                buyIndex+=1
                i+=1
            sellIndex = buyIndex
            while i < L -1 and A[i+1] >= A[i]:
                sellIndex+=1
                i+=1
            res+=A[sellIndex]-A[buyIndex]
        return res
            

